import{_ as a,c as l,o as r,ag as t}from"./chunks/framework.CA5djfbr.js";const p=JSON.parse('{"title":"Ubuntu24 系统安装和配置","description":"","frontmatter":{},"headers":[],"relativePath":"tutorial/Linux/ubuntu_configure.md","filePath":"tutorial/Linux/ubuntu_configure.md","lastUpdated":1760970394000}'),o={name:"tutorial/Linux/ubuntu_configure.md"};function i(h,e,n,s,u,c){return r(),l("div",null,[...e[0]||(e[0]=[t(`<h1 id="ubuntu24-系统安装和配置" tabindex="-1">Ubuntu24 系统安装和配置 <a class="header-anchor" href="#ubuntu24-系统安装和配置" aria-label="Permalink to &quot;Ubuntu24 系统安装和配置&quot;">​</a></h1><h2 id="说明" tabindex="-1">说明 <a class="header-anchor" href="#说明" aria-label="Permalink to &quot;说明&quot;">​</a></h2><ul><li>进行 ubntu 系统安装需要在 windows 环境下</li><li>ubuntu 相比于 debian,在桌面环境下更加的简单易用,会避免很多的配置问题</li></ul><h2 id="下载镜像" tabindex="-1">下载镜像 <a class="header-anchor" href="#下载镜像" aria-label="Permalink to &quot;下载镜像&quot;">​</a></h2><p><a href="https://ubuntu.com/download/desktop" target="_blank" rel="noreferrer">ubuntu 桌面版</a></p><h2 id="烧录镜像到-u-盘" tabindex="-1">烧录镜像到 U 盘 <a class="header-anchor" href="#烧录镜像到-u-盘" aria-label="Permalink to &quot;烧录镜像到 U 盘&quot;">​</a></h2><p><a href="https://rufus.ie/zh/#download" target="_blank" rel="noreferrer">rufus 烧录工具</a></p><p>选好镜像文件,点击开始就可以了 烧录时间和 U 盘的速度有关,大概 30 分钟左右</p><h2 id="安装系统" tabindex="-1">安装系统 <a class="header-anchor" href="#安装系统" aria-label="Permalink to &quot;安装系统&quot;">​</a></h2><p>将 U 盘插入目标电脑,选择 U 盘启动,根据提示一步一步配置</p><h2 id="系统配置" tabindex="-1">系统配置 <a class="header-anchor" href="#系统配置" aria-label="Permalink to &quot;系统配置&quot;">​</a></h2><h3 id="配置-clash-verge" tabindex="-1">配置 clash-verge <a class="header-anchor" href="#配置-clash-verge" aria-label="Permalink to &quot;配置 clash-verge&quot;">​</a></h3><ul><li>登陆腾讯云对象存储下载 deb 版本文件和配置文件</li><li><a href="https://github.com/clash-verge-rev/clash-verge-rev/releases" target="_blank" rel="noreferrer">clash-verge 版本仓库下载</a></li></ul><h3 id="配置-chrome" tabindex="-1">配置 chrome <a class="header-anchor" href="#配置-chrome" aria-label="Permalink to &quot;配置 chrome&quot;">​</a></h3><ul><li>使用 ubuntu 自带的浏览器搜索 <code>chrome下载</code></li><li><a href="https://www.google.com/chrome/dr/download" target="_blank" rel="noreferrer">chrome 官方下载地址</a></li><li>登陆 google 账户</li></ul><h3 id="gnome-shell-集成扩展" tabindex="-1">GNOME Shell 集成扩展 <a class="header-anchor" href="#gnome-shell-集成扩展" aria-label="Permalink to &quot;GNOME Shell 集成扩展&quot;">​</a></h3><h4 id="chrome-安装浏-gnome-shell-扩展" tabindex="-1">chrome 安装浏 GNOME Shell 扩展 <a class="header-anchor" href="#chrome-安装浏-gnome-shell-扩展" aria-label="Permalink to &quot;chrome 安装浏 GNOME Shell 扩展&quot;">​</a></h4><ul><li><a href="https://extensions.gnome.org/" target="_blank" rel="noreferrer">GNOME Shell 官网</a>: 使用 chrome 访问官网会自动提示安装浏览器扩展</li><li><a href="https://chromewebstore.google.com/detail/gnome-shell-integration/gphhapmejobijbbhgpjhcjognlahblep?pli=1" target="_blank" rel="noreferrer">GNOME Shell 集成 扩展地址</a></li></ul><h4 id="安装-gnome-shell-连接器" tabindex="-1">安装 GNOME Shell 连接器 <a class="header-anchor" href="#安装-gnome-shell-连接器" aria-label="Permalink to &quot;安装 GNOME Shell 连接器&quot;">​</a></h4><ul><li><code>sudo apt install gnome-brower-connector </code></li></ul><blockquote><p>安装完成后，系统会多出一个叫<strong>扩展</strong>的应用，这个应用就是管理 GNOME Shell 扩展扩展的，从 GNOME Shell 官网上下载的扩展都会显示在这里 GNOME Shell 官网上的扩展直接点击安装就可以，要注意是否兼容，是否能正常工作</p></blockquote><h2 id="gnome-插件推荐" tabindex="-1">GNOME 插件推荐 <a class="header-anchor" href="#gnome-插件推荐" aria-label="Permalink to &quot;GNOME 插件推荐&quot;">​</a></h2><ul><li><p><a href="https://extensions.gnome.org/extension/6682/astra-monitor/" target="_blank" rel="noreferrer">Astra Monitor</a>: Astra Monitor 是 GNOME 顶部栏的尖端、完全可定制且以性能为中心的系统监控扩展。对于那些寻求密切关注系统性能指标（如 CPU、GPU、RAM、磁盘使用情况、网络统计数据和传感器读数）的人来说，它是一款一体化解决方案。</p></li><li><p><a href="https://extensions.gnome.org/extension/4839/clipboard-history/" target="_blank" rel="noreferrer">Clipboard History</a>: 剪贴板历史记录是一个剪贴板管理器 GNOME 扩展，可将您复制的项目保存到易于访问、可搜索的历史记录面板中。</p></li></ul><h2 id="软件推荐" tabindex="-1">软件推荐 <a class="header-anchor" href="#软件推荐" aria-label="Permalink to &quot;软件推荐&quot;">​</a></h2><ul><li><p><a href="https://code.visualstudio.com/docs/setup/linux" target="_blank" rel="noreferrer">VScode 下载地址</a></p><ul><li><p>下载后登陆 github 账号,同步配置</p></li><li><p>配置 <code>git</code> - 安装<code>git</code>: <code>sudo install git</code> - 配置账户和邮箱:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> user.name</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;george&quot;</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> user.email</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;nihaokexiao@gmali.com&quot;</span></span></code></pre></div></li></ul></li><li><p><code>glate</code>: 简单的划词翻译软件,快捷键 <code>ctrl alt space</code></p><ul><li>应用商店直接搜索 <code>glate</code>, 直接安装即可</li><li><a href="https://github.com/keshavbhatt/glate" target="_blank" rel="noreferrer">仓库地址</a></li></ul></li></ul>`,25)])])}const g=a(o,[["render",i]]);export{p as __pageData,g as default};
