import{_ as l,c as i,o as e,ag as t}from"./chunks/framework.DSvwVc1k.js";const u=JSON.parse('{"title":"短视频、直播打造","description":"","frontmatter":{},"headers":[],"relativePath":"MyThink/家具思考/短视频拍摄直播.md","filePath":"MyThink/家具思考/短视频拍摄直播.md","lastUpdated":1750731332000}'),r={name:"MyThink/家具思考/短视频拍摄直播.md"};function o(h,a,n,p,d,s){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="短视频、直播打造" tabindex="-1">短视频、直播打造 <a class="header-anchor" href="#短视频、直播打造" aria-label="Permalink to &quot;短视频、直播打造&quot;">​</a></h1><h2 id="挖掘人物独特的特点-成为视频和直播的看点之一" tabindex="-1">挖掘人物独特的特点，成为视频和直播的看点之一 <a class="header-anchor" href="#挖掘人物独特的特点-成为视频和直播的看点之一" aria-label="Permalink to &quot;挖掘人物独特的特点，成为视频和直播的看点之一&quot;">​</a></h2><p>可挖掘的特点</p><ul><li>核心是通过声音、表情、动作传递情绪</li><li>表情丰富（憨豆先生、金凯瑞）（杨迪、金）</li><li>多方言，广东+四川</li></ul><h2 id="对标账号" tabindex="-1">对标账号 <a class="header-anchor" href="#对标账号" aria-label="Permalink to &quot;对标账号&quot;">​</a></h2><ul><li>电器哥赵北</li></ul><h2 id="视频结构设计" tabindex="-1">视频结构设计 <a class="header-anchor" href="#视频结构设计" aria-label="Permalink to &quot;视频结构设计&quot;">​</a></h2><ul><li><p>口头禅：</p><ul><li>家具不用想，直接找老蒋</li><li>家具还在想，那就找老蒋</li><li>家具想一想，还是找老蒋</li><li>关注老蒋，家具帮你想</li><li>生意不好做，只有来处货（天天来处货）</li></ul></li><li><p>开头提出疑问（痛点），然后进入主题（阿爽）</p></li><li><p>口播加图片视频，GPT 文案撰写，AI 视频、图片生成，一个主题，口语化（普通话、四川）（有感情）一个、诗歌化一个，唱歌一个（对口型），而且可以顺便就做成产品介绍卡</p></li><li><p>口播一条，知识讲解，产品推荐</p></li><li><p>处货一条，性价比产品，特价产品</p></li><li><p>服化道风格：同志们（革命年代的风格）、cos 李小龙</p></li><li><p>开头情绪爆炸，直接大声音、夸张动作开场</p></li><li><p>反差（包袱）</p></li></ul><h2 id="后期剪辑" tabindex="-1">后期剪辑 <a class="header-anchor" href="#后期剪辑" aria-label="Permalink to &quot;后期剪辑&quot;">​</a></h2><h2 id="方言特殊词句收集" tabindex="-1">方言特殊词句收集 <a class="header-anchor" href="#方言特殊词句收集" aria-label="Permalink to &quot;方言特殊词句收集&quot;">​</a></h2><h3 id="四川" tabindex="-1">四川 <a class="header-anchor" href="#四川" aria-label="Permalink to &quot;四川&quot;">​</a></h3><p>老贼，灾贼、瓜娃子、龟儿子，灾舅子，狗（苟）老表，鸡毛，灾鸡娃子，哇哇叫，毛线，不虚，仙人板板，鸡儿屎，砸啦花儿，日白，吹壳子，舍娃儿，扯火，扯把子，背时，宝批龙，胎神，神头儿，爆参子，莽子娃儿，温桑，二杆子，包谷娃，花苞谷</p><p>好大个烟锅吧踩不息</p><p>老子看你日不弄怂，猫钻灶孔，又穷又饿不吃豆芽脚脚，一天左脚打右脚鸭儿钏脑壳，球钱不带还想吃炒菜，分钱不花还想做空调大巴。</p><p>人又长得丑价钱忙起吼，人又长得撇那里都敢去，正事不做豆腐放醋。说你瓜你就瓜，别个的婆娘你喊妈</p><p>老子天不怕地不怕，就怕你屋头的婆娘打光胯，看你一天想惊想怪还想看王母娘娘穿吊带。</p><h3 id="广东" tabindex="-1">广东 <a class="header-anchor" href="#广东" aria-label="Permalink to &quot;广东&quot;">​</a></h3><p>妈耶，抠老爷</p>',18)]))}const _=l(r,[["render",o]]);export{u as __pageData,_ as default};
